import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { UserService, User } from '../services/user.service';
import { UserDetailsComponent } from '../user-details/user-details.component';

@Component({
  selector: 'app-users',
  templateUrl: './user.page.html',
  styleUrls: ['./user.page.scss'],
  standalone:false,
})
export class UsersPage implements OnInit {
  users: User[] = [];
  isLoading = true;

  constructor(private userService: UserService, private modalCtrl: ModalController) {}

  ngOnInit() {
    this.loadUsers();
  }

  // Cargar usuarios desde la API
  loadUsers() {
    this.userService.getUsers().subscribe({
      next: (data) => {
        this.users = data;
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error al cargar usuarios:', error);
        this.isLoading = false;
      },
    });
  }

  // Abrir el modal con los detalles del usuario
  async openUserDetails(user: User) {
    const modal = await this.modalCtrl.create({
      component: UserDetailsComponent,
      componentProps: { user },
    });
    await modal.present();
  }
}
<ion-header [translucent]="true">

  <ion-toolbar>
    <ion-title size="large" class="titleu" >Usuarios</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title >user</ion-title>
    </ion-toolbar>
  </ion-header>
  <!-- Spinner mientras carga -->
  <ion-spinner *ngIf="isLoading" color="primary"></ion-spinner>

  <!-- Lista de usuarios -->
  <ion-list *ngIf="!isLoading">
    <ion-item *ngFor="let user of users" (click)="openUserDetails(user)">
      <ion-label>
        <h2>{{ user.name }}</h2>
        <p>{{ user.username }}</p>
      </ion-label>
    </ion-item>
  </ion-list>
</ion-content>
