import { Component } from '@angular/core';

import { Router } from '@angular/router';


@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html',
  styleUrls: ['app.component.scss'],
  standalone: false,
})
export class AppComponent {


  showMenu = true;
  email: string;
  password: string;

  constructor(private router: Router) {
    // Detectar cambios en la ruta
    this.router.events.subscribe(() => {
      this.showMenu = !this.router.url.includes('/login'); // Oculta el menú en la página de Login
    });
    this.email = 'test@example.com';
    this.password = 'admin';
  }

  login() {
    console.log('Email:', this.email);
    console.log('Password:', this.password);
    // Aquí puedes agregar la lógica para manejar el inicio de sesión
  }
}

<ion-router-outlet id="main-content"></ion-router-outlet>

<!-- Menú lateral -->
<ion-menu side="start" menuId="first" contentId="main-content" *ngIf="showMenu">
  <ion-header>
    <ion-toolbar>
      <ion-title>Menú</ion-title>
    </ion-toolbar>
  </ion-header>
  
  <ion-content>
    <ion-list>
      <ion-item routerLink="/user">
        <ion-label>User</ion-label>
      </ion-item>
     
      <ion-item routerLink="/login">
        <ion-label>Salir</ion-label>
      </ion-item>
    </ion-list>
  </ion-content>
</ion-menu>

<!-- Botón para abrir el menú -->
<ion-buttons slot="start" *ngIf="showMenu">
  <ion-menu-button></ion-menu-button>
</ion-buttons>
